import { useEffect, useState } from 'react';
import './App.css';

function App() {

  const [records, setRecords] = useState([]);
  const user = btoa('candidate:12345');

  useEffect(() => {
    fetch('https://api.test-sfx-hub.co.za/api/utility/province', {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Basic ${user}`, 
      },
      method: "GET"
    })
    .then(response => response.json())
    .then(data => setRecords(data))
    .catch(err => console.log(err))
  }, [])

  console.log(records)

  return (
    <div className="App">
      <h1 style={{ color: 'silver' } }>Coding Challenge 2023</h1>
      {user}
      <table>
        <ul>
          {records.map}
        </ul>
      </table>
    </div>
  );
}

export default App;
